            <p>This article has {articleInfo.upvotes} upvote(s)</p>
            {article.content.map(paragraph => (
                <p key={paragraph}>{paragraph}</p>
            ))}
            <CommentsList comments={articleInfo.comments} />
        </>
    )
}

export default ArticlePage;
